import React, {useState, useEffect} from "react"

const Context = React.createContext()

function ContextProvider({children}) {
    const [product, setProduct] = useState([])
    const [search, setSearch] = useState("sly cooper")
    const url = `https://svcs.ebay.com/services/search/FindingService/v1?OPERATION-NAME=findItemsByKeywords&SERVICE-VERSION=1.0.0&SECURITY-APPNAME=KarimZit-TestKey-PRD-83882c164-20d40dd0&RESPONSE-DATA-FORMAT=JSON&REST-PAYLOAD&keywords=${search}&itemFilter.name=MaxPrice&itemFilter.value=10.00&itemFilter.paramName=Currency&itemFilter.paramValue=USD&paginationInput.entriesPerPage=15`
    useEffect( () => {
      async function fetchData(){
        const res = await fetch(url);
        res.json()
        .then(data => setProduct(data.findItemsByKeywordsResponse)) 
    } 
  fetchData()
}, [])

function onSubmit(item) {
    setSearch(item)

    fetchData()
}
    return (
        <Context.Provider value={{product, onSubmit}}>
            {children}
        </Context.Provider>
    )
}


export {ContextProvider, Context}
