import React, {useState, useEffect} from "react"

const Context = React.createContext()

function ContextProvider({children}) {
    const [product, setProduct] = useState([])
    const [search, setSearch] = useState("sly cooper")
    const [category, setCategory] = useState("")
    const [item, setItem] = useState([])
    const url = `https://svcs.ebay.com/services/search/FindingService/v1?OPERATION-NAME=findItemsAdvanced&SERVICE-VERSION=1.0.0&SECURITY-APPNAME=KarimZit-TestKey-PRD-83882c164-20d40dd0&RESPONSE-DATA-FORMAT=JSON${category}&REST-PAYLOAD&keywords=${search}&itemFilter.name=MaxPrice&itemFilter.value=1000.00&itemFilter.paramName=Currency&itemFilter.paramValue=USD&outputSelector(0)=SellerInfo&outputSelector(1)=CategoryHistogram&paginationInput.entriesPerPage=35`
    
    const singleurl = "https://open.api.ebay.com/shopping?callname=GetSingleItem&responseencoding=JSON&appid=KarimZit-TestKey-PRD-83882c164-20d40dd0&siteid=0&version=967&ItemID=322546733868&IncludeSelector=Description,ItemSpecifics,ShippingCosts,Details,Variations"
    useEffect( () => {
      async function fetchData(){
        const res = await fetch(url);
        res.json()
        .then(data => setProduct(data.findItemsAdvancedResponse)) 
    }
  fetchData()
  console.log(category)

}, [search, category])

 async function itemFetch() {
    const res = await fetch(singleurl);
    res.json()
    .then(data => setItem(data))
console.log(item) 
}

function onSubmit(item, cat) {
    setSearch(item)
    setCategory(cat)
    console.log(cat + "Category")
}

function onClick(item) {
    setCategory(item)

    console.log(category)
}
    return (
        <Context.Provider value={{product, onSubmit, category, onClick, itemFetch}}>
            {children}
        </Context.Provider>
    )
}


export {ContextProvider, Context}
