import React, {useState, useContext} from 'react'
import SearchResult from './SearchResult'
import SampleData from './SampleData'
import {Context} from './Context'
import AltSearchResult from './AltSearchResult'

const Result = () => {
    const {product} = useContext(Context)
    
    const newarr = []
    // for(let i = 0; i<2; i++){
    //  newarr.push(product.searchResult[0].item[i].galleryURL)
    //     }
    let Elements
    if(product.searchResult[0].length >0){
     Elements = product.map((item) => (
        <div>
            {item.searchResult[0].item.map((t) => (
                <SearchResult item={t.galleryURL} title={t.title} subtitle={t.subtitle} conditionDisplayName={t.conditionDisplayName} price={t.sellingStatus[0].currentPrice[0].__value__} />

                ))
        }
        </div>
    )) }
    else {Elements = <h1>no Search</h1>}
    return (
        <div >
            {Elements}
        </div>
    )
}

export default Result
