import React from 'react'

const Api = 'https://swapi.co/api/people/1'

export default class Starwars extends React.Component {
    constructor(){
        super()
        this.state={
          person : ""
        }
      }
      componentDidMount() {
        fetch(Api)
          .then(response => response.json())
          .then(data => this.setState({ person: data.person }));
      }
    render(){
        return (<div>
            <h1>
            {this.state.person}
            </h1>
        </div>)
    }
}
