import React from 'react'
import {IoIosSearch} from "react-icons/io";
const Searchbar = () => {

    return (
        <div>
            <input style = {Inputbar} placeholder="Search"/>
                <select style = {Categorybar} name="Cat" placeholder="Ca">
                <option>Categories</option>
                </select>
                <IoIosSearch style= {Searchicon}/>
        </div>
    )
}
 
const Inputbar = {
    height: '2rem',
    width: '20rem',
    textAlign: 'center',
    borderBottomColor: 'yellow',
    borderWidth: 'thin thin .2rem thin'
}
const Categorybar = {
    height: '2.4rem',
    width: '10rem',
    borderBottomColor: 'green',
    borderWidth: 'thin thin .2rem thin'
}
const Searchicon = {
    textAlign: 'center',
    justifyContent: 'center',
    alignContents: 'center',
    alignItems: 'center',
    color:'white'
}
export default Searchbar
