import React, {useContext, useState} from 'react'
import {IoIosSearch} from "react-icons/io";
import {Context} from './Context'
const Searchbar = () => {
    const {onSubmit} = useContext(Context)
    const [search, setSearch] = useState("")
    return (
        <header style={header}>
            <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/48/EBay_logo.png/128px-EBay_logo.png" style={{}}/>
            <input style = {Inputbar} value={search} onChange={(e) => setSearch(e.target.value)} placeholder="Search"/>
                <select style = {Categorybar} name="Cat" placeholder="Ca">
                <option>Categories</option>
                </select>
                <span  style={{backgroundColor:'green', textAlign:'center', justifyContent:'center', flex: 1, alignItems: 'center',paddingTop:".37rem", paddingBottom:".48rem"}}>
                <IoIosSearch size={35} style= {Searchicon}/>
                <button onClick={()=> onSubmit(search)}/>
                </span>
        </header>
    )
}
 
const header = {
    textAlign:'left',
    marginLeft:'8rem',
}
const Inputbar = {
    height: '2rem',
    flex:1, 
    width: '25rem',
    marginLeft:'4rem',
    textAlign: 'center',
    borderBottomColor: 'yellow',
    borderWidth: 'thin thin .2rem thin'
}
const Categorybar = {
    height: '2.4rem',
    width: '10rem',
    flex: 1,
    borderBottomColor: 'blue',
    borderWidth: 'thin thin .2rem thin'
}
const Searchicon = {
    width:'2rem',
    height:"1rem",
}
export default Searchbar
