import React from 'react'
import {IoIosSearch} from "react-icons/io";
const Searchbar = () => {

    return (
        <header>
            <input style = {Inputbar} placeholder="Search"/>
                <select style = {Categorybar} name="Cat" placeholder="Ca">
                <option>Categories</option>
                </select>
                <span style={{backgroundColor:'green', textAlign:'center', justifyContent:'center',  height:'8px', width:'4rem', padding:'20', flex: 1, alignItems: 'center',}}>
                <IoIosSearch size={20} style= {Searchicon}/>
                </span>
        </header>
    )
}
 
const Inputbar = {
    height: '2rem',
    flex:1, 
    width: '20rem',
    textAlign: 'center',
    borderBottomColor: 'yellow',
    borderWidth: 'thin thin .2rem thin'
}
const Categorybar = {
    height: '2.4rem',
    width: '10rem',
    flex: 1,
    borderBottomColor: 'blue',
    borderWidth: 'thin thin .2rem thin'
}
const Searchicon = {
    width:'2rem',
    flex:1,
    paddingTop:'5rem'
}
export default Searchbar
