import React, {useState, useEffect} from "react"

const Context = React.createContext()

function ContextProvider({children}) {
    const [product, setProduct] = useState([])
    const [search, setSearch] = useState("sly cooper")
    const [category, setCategory] = useState(null)
    const url = `https://svcs.ebay.com/services/search/FindingService/v1?OPERATION-NAME=findItemsAdvanced&SERVICE-VERSION=1.0.0&SECURITY-APPNAME=KarimZit-TestKey-PRD-83882c164-20d40dd0&RESPONSE-DATA-FORMAT=JSON&${category}&REST-PAYLOAD&keywords=${search}&itemFilter.name=MaxPrice&itemFilter.value=1000.00&itemFilter.paramName=Currency&itemFilter.paramValue=USD&outputSelector(0)=SellerInfo&outputSelector(0)=CategoryHistogram&paginationInput.entriesPerPage=35`
    useEffect( () => {
      async function fetchData(){
        const res = await fetch(url);
        res.json()
        .then(data => setProduct(data.findItemsAdvancedResponse)) 
    }
  fetchData()
}, [search, category])

function onSubmit(item) {
    setSearch(item)
    
    console.log(product)
}

function onClick(item) {
    setCategory(item)

    console.log(category)
}
    return (
        <Context.Provider value={{product, onSubmit, category, onClick}}>
            {children}
        </Context.Provider>
    )
}


export {ContextProvider, Context}
